import { useState } from "react";

const SimpleInput = (props) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [isEnteredValueValid, setIsEnteredValueValid] = useState(true);
  const [isEnteredValueTouched, setIsEnteredValueTouched] = useState(false);

  const nameInputHandler = (event) => {
    setEnteredValue(event.target.value);
    setIsEnteredValueTouched(true);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();

    if (enteredValue.trim() === "") {
      setIsEnteredValueValid(false);
      return;
    }
    console.log(enteredValue);
    setEnteredValue("");
  };

  const nameInputIsInvalid = !isEnteredValueValid && isEnteredValueTouched;

  const nameInputClasses = isEnteredValueValid ? "form-control" : "form-control invalid";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" value={enteredValue} onChange={nameInputHandler} />
        {!isEnteredValueValid && <p className="error-text">Please enter a name.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
